import React, { useState, useEffect } from 'react'
import "../styles/flavors.css"
import { GetQuote } from './GetQuote'
import { useParams } from 'react-router-dom';

export const Flavors = () => {

  const { category } = useParams();
  const [flavors, setFlavors] = useState(null)

  useEffect(() => {
    window.scrollTo(0, 0); 
  }, );

  useEffect(() => {
    const fetchCategoryFlavor = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/flavors/${category}`);
        if (response.ok) {
          const flavorArray = await response.json();
          setFlavors(flavorArray);
        } else {
          console.log(`Failed to fetch categories. Status: ${response.status}`);
        }
      } catch (error) {
        console.log(error);
      }
    };

    fetchCategoryFlavor()
  }, []);
  
  return (
    <>
      <div className='categoryContainer'>
        <div className="categoryFlavors">
        <h1 className="categoryHeader">
          All {category} Flavors
        </h1>
          {flavors && flavors.map((flavor, index) => (
            <div className="individualFlavors" key={index}> 
              <ul>
                <li>
                  {flavor.flavorName}
                </li>
              </ul>
            </div>
          ))}
        </div> 
        <div className="quoteBox">
          <GetQuote />
        </div>
      </div> 
    </>
  );
}

<div className="navContainer">
      <Link id='logoLink' to="/">
        <img src={logo} alt="Company Logo" />
      </Link>
      <nav>
        <ul>
        <li className='hoverLink'>
            <Link to="/">
              Home
            </Link>
          </li>
          <li>    
            <NavDropdown title="Capabilities">
              <NavDropdown.Item href="">
                Design
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Production
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Certification
              </NavDropdown.Item>
            </NavDropdown>
          </li>
          <li>
          <NavDropdown title="Flavors">
          <NavDropdown.Item href= "/categories">
            All Categories
          </NavDropdown.Item>
              {categories && categories.map((category) => (
                link = `/flavors/${category}`,
                <NavDropdown.Item href={link} key={category}>
                  {category}
                </NavDropdown.Item>
              ))}
          </NavDropdown>
          </li>
          <li className='hoverLink'>
            <Link to="/">
              About us
            </Link>
          </li>
          <li className='hoverLink'>
          <Link to={{ pathname: '/', search: '?source=contact' }}>
              Contact Us
            </Link>
          </li>
        </ul>
      </nav>
