import React from 'react'
import { useEffect, useState } from 'react'
import "../../styles/home.css"
import { HomeCarousel } from './HomeCarousel'
import { Contact } from './Contact'
import { GetQuote } from '../GetQuote'
import { useLocation } from 'react-router-dom';


export const Home = () => {

  const location = useLocation();



  function checkSource() {
    
    const searchParams = new URLSearchParams(location.search);
    const source = searchParams.get('source');

    if (source === 'contact') {
      const targetElement = document.getElementById('contactTarget');
      if (targetElement) {
        console.log("Triggered")
        targetElement.scrollIntoView({ behavior: 'smooth', block: "end",});
      }
    }
  }
 
  /*If setTimeout is not used, the page will quickly flick back to top of page if you enter from anything other than the home page. Bug needs to be fixed...*/
  useEffect(() => {
    setTimeout(checkSource, 500)

  }, );




  return (
    <div>
      <HomeCarousel />
      <div className="homeContainer">
