import React from 'react'
import { useState } from 'react'

export const GetQuote = () => {

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    capability: "",
    message: "",
    updates: false
  })

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData)
  /*
    try {

      const quote = { fullName, email, capability, message };
      console.log(quote)
      
      const response = await fetch('http://localhost:4000/api/quote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(quote),
      });
      setFormData({
        name: "",
        email: "",
        capability: "",
        message: "",
      });
    } catch (err) {
      console.log(err);
    }
    */
  };
    
      const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (e.target.type === 'select-one') {
          const selectedOption = e.target.options[e.target.selectedIndex];
          setFormData({
            ...formData,
            [name]: selectedOption.value,
          });
        } else {
          setFormData({
            ...formData,
            [name]: value,
          });
        };
      };

      const handleCheckboxChange = (e) => {
        const { name, checked } = e.target;
      
        setFormData({
          ...formData,
          [name]: checked,
        });
      };

  return (
    <div className="getQuote">
    <form className="formContainer" onSubmit={handleSubmit}>
      <h1 className="formHeader">Get a Quote</h1>
      <input 
        type="text" 
        className="formInput" 
        placeholder="Name"
        name='name'
        value={formData.name}
        onChange={handleInputChange}/>
      <input 
        type="email" 
        className="formInput" 
        name='email'
        placeholder="Email"
        value={formData.email}
        onChange={handleInputChange}/>
      <select 
        className="formDropdown"
        name="capability"
        value={formData.capability}
        onChange={handleInputChange}
      >
        <option value={""} disabled>Select a capability</option>
        <option value="Design">Design</option>
        <option value="Production">Production</option>
        <option value="Certification">Certification</option>
      </select>
      <textarea 
        className="formTextarea" 
        placeholder="Message"
        name='message'
        value={formData.message}
        onChange={handleInputChange}>
      </textarea>
      <label htmlFor="formCheckbox">Would you like to receive email updates?</label>
      <input 
      type="checkbox" 
      id="formCheckbox" 
      name="formCheckbox"
      checked={formData.updates}
      onChange={handleCheckboxChange}/>
      <button className="formButton">Get a Quote</button>
    </form>
    </div>
  )
}
