import React from 'react';
import { useState, useEffect } from 'react';
import "./../../styles/navbar.css"
import logo from "./logo.png"
import { Link, useLocation } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

export const Navigation = () => {

  let link;

  const [categories, setCategories] = useState(null)
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:4000/api/flavors/Categories');
        if (response.ok) {
          const categoriesArray = await response.json();
          setCategories(categoriesArray);
        } else {
          console.log(`Failed to fetch categories. Status: ${response.status}`);
        }
      } catch (error) {
        console.log(error);
      }
    };
  
    fetchCategories(); 
  }, []);

  return (
      <Navbar expand="lg" className="bg-body-tertiary">
        <Container fluid>
        <div className="navContainer">
      <Link id='logoLink' to="/">
        <img src={logo} alt="Company Logo" />
      </Link>
      <nav>
        <ul>
        <li className='hoverLink'>
            <Link to="/">
              Home
            </Link>
          </li>
          <li>    
            <NavDropdown title="Capabilities">
              <NavDropdown.Item href="">
                Design
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Production
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Certification
              </NavDropdown.Item>
            </NavDropdown>
          </li>
          <li>
          <NavDropdown title="Flavors">
          <NavDropdown.Item href= "/categories">
            All Categories
          </NavDropdown.Item>
              {categories && categories.map((category) => (
                link = `/flavors/${category}`,
                <NavDropdown.Item href={link} key={category}>
                  {category}
                </NavDropdown.Item>
              ))}
          </NavDropdown>
          </li>
          <li className='hoverLink'>
            <Link to="/">
              About us
            </Link>
          </li>
          <li className='hoverLink'>
          <Link to={{ pathname: '/', search: '?source=contact' }}>
              Contact Us
            </Link>
          </li>
        </ul>
      </nav>
      </div>
        </Container>
      </Navbar>
    );
      
}
